    for(k=p;k<=q;k++)
    {
        //å½“LLä¸RRæ²¡æœ‰å…ƒç´ æ—¶ï¼Œæ”¾å…¥åŸæ¥æ•°ç»„
        if(i==L)
        {
            arr[k-1][1]=RR[j][1];
            arr[k-1][0]=RR[j][0];
            j++;

        }
        else if(j==R)
        {
